import React, { useState } from "react";
import fetch from "fetch";

const AddProducts = () => {

const [title, setTitle] = useState("");
const [price, setPrice] = useState("");
const [description, setDescription] = useState("");
const [category, setCategory] = useState("");
const [image, setImage] = useState("");

const handleSubmit = async (e) => {
  e.preventDefault();

  const data = {
    title,
    price,
    description,
    category,
    image,
  };

  const response = await fetch("/api/products", {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify(data),
  });

  if (response.status === 200) {
    // Success
  } else {
    // Error
  }
};

  return (
    <section className="vh-100">
      <div className="mask d-flex align-items-center h-100 gradient-custom-3">
        <div className="container h-95">
          <div className="row d-flex justify-content-center align-items-center h-100">
            <div className="col-12 col-md-9 col-lg-7 col-xl-6">
              <div className="card  border rounded-lg border-info">
                <div className="card-body p-5 ">
                  <h2 className="text-center mb-5">Add Product</h2>
                  <form onSubmit={handleSubmit}>
                    <div className="form-group form-outline mb-3">
                      <label htmlFor="title">Title</label>
                      <input
                        type="text"
                        className="form-control form-outline mb-3"
                        id="title"
                        name="title"
                        value={title}
                        onChange={(e) => setTitle(e.target.value)}
                      />
                    </div>
                    <div className="form-group form-outline mb-3">
                      <label htmlFor="price">Price</label>
                      <input
                        type="number"
                        className="form-control form-outline mb-3"
                        id="price"
                        name="price"
                        value={price}
                        onChange={(e) => setPrice(e.target.value)}
                      />
                    </div>
                    <div className="form-group form-outline mb-3">
                      <label htmlFor="description">Description</label>
                      <textarea
                        className="form-control"
                        id="description"
                        name="description"
                        value={description}
                        onChange={(e) => setDescription(e.target.value)}
                      ></textarea>
                    </div>
                    <div className="form-group form-outline mb-3">
                      <label htmlFor="category">Category</label>
                      <input
                        type="text"
                        className="form-control"
                        id="category"
                        name="category"
                        value={category}
                        onChange={(e) => setCategory(e.target.value)}
                      />
                    </div>
                    <div className="form-outline mb-3">
                      <label className="form-label">Upload Product Image</label>
                      <input
                        type="file"
                        className="form-control border border-info form-control-lg"
                        id="exampleFormControlFile1"
                        name="image"
                        value={image}
                        onChange={(e) => setImage(e.target.value)}
                      />
                    </div>
                    <div className="d-flex justify-content-between ">
                      <button
                        type="submit"

                        className="btn btn-info mr-2"
                      >
                        Add Product
                      </button>
                      <button type="reset" className="btn btn-secondary ml-2">
                        Reset
                      </button>
                    </div>
                  </form>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default AddProducts;
