import React, { useEffect, useState } from 'react';

const UpdateProduct = () => {
  const [productData, setProductData] = useState({
    title: '',
    price: 0,
    description: '',
    image: '',
    category: ''
  });

  useEffect(() => {
    const fetchProductData = async () => {
      try {
        const response = await fetch('https://fakestoreapi.com/products/22');
        const data = await response.json();
        setProductData(data);
      } catch (error) {
        console.log(error);
      }
    };

    fetchProductData();
  }, []);

  const updateProduct = () => {
    fetch('https://fakestoreapi.com/products/22', {
      method: "PUT",
      body: JSON.stringify(productData)
    })
      .then(res => res.json())
      .then(json => console.log(json))
      .catch(error => console.log(error));
      console.log(productData); 
  };

  const handleInputChange = (event) => {
    const { name, value, files } = event.target;
    if (name === "image") {
      setProductData(prevData => ({
        ...prevData,
        image: files[0]
      }));
    } else {
      setProductData(prevData => ({
        ...prevData,
        [name]: value
      }));
    }
  };

  return (
    <section className="vh-100">
      <div className="mask d-flex align-items-center h-100 gradient-custom-3">
        <div className="container h-95">
          <div className="row d-flex justify-content-center align-items-center h-100">
            <div className="col-12 col-md-9 col-lg-7 col-xl-6">
              <div className="card border rounded-lg border-info">
                <div className="card-body p-5">
                  <h2 className="text-center mb-5">Update Product</h2>

                    <form >
                      <div className="form-group form-outline mb-3">
                        <label htmlFor="title">Title</label>
                        <input
                          type="text"
                          className="form-control form-outline mb-3"
                          id="title"
                          name="title"
                          value={productData.title}
                          onChange={handleInputChange}
                        />
                      </div>
                      <div className="form-group form-outline mb-3">
                        <label htmlFor="price">Price</label>
                        <input
                          type="number"
                          className="form-control form-outline mb-3"
                          id="price"
                          name="price"
                          value={productData.price}
                          onChange={handleInputChange}
                        />
                      </div>
                      <div className="form-group form-outline mb-3">
                        <label htmlFor="description">Description</label>
                        <textarea
                          className="form-control"
                          id="description"
                          name="description"
                          value={productData.description}
                          onChange={handleInputChange}
                        ></textarea>
                      </div>
                      <div className="form-group form-outline mb-3">
                        <label htmlFor="category">Category</label>
                        <input
                          type="text"
                          className="form-control"
                          id="category"
                          name="category"
                          value={productData.category}
                          onChange={handleInputChange}
                        />
                      </div>
                      <div className="form-outline mb-3">
                        <label className="form-label">
                          Upload Product Image
                        </label>
                        <input
                          type="file"
                          className="form-control border border-info form-control-lg"
                          id="exampleFormControlFile1"
                          name="image"
                          accept="image/*"
                          onChange={handleInputChange}
                        />
                      </div>
                      <div className="d-flex justify-content-between">
                        <button type="submit" onClick={updateProduct} className="btn btn-info mr-2">
                          Update Product
                        </button>
                        <button type="reset" className="btn btn-secondary ml-2">
                          Reset
                        </button>
                      </div>
                    </form>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default UpdateProduct;
