import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams } from 'react-router-dom';


const UpdateProduct = () => {
  const { id } = useParams();

  const [formData, setFormData] = useState({
    title: "",
    price: "",
    description: "",
    category: "",
    image: "",
  });

  useEffect(() => {
    // Fetch the existing product data and update the form fields
    const fetchProductData = async () => {
      try {
        const productId = "replace_with_product_id"; // Replace with the actual product ID

        // Make a GET request to retrieve the existing product data
        const response = await axios.get(`http://localhost:8000/product/${id}`);

        // Update the form data with the retrieved product data
        setFormData({
          title: response.data.title,
          price: response.data.price,
          description: response.data.description,
          category: response.data.category,
          image: null, // Since we don't want to pre-fill the image field
        });
      } catch (error) {
        // Handle any errors that occurred during the API request
        console.log(error);
      }
    };

    fetchProductData();
  }, []);

  const handleInputChange = (event) => {
    if (event.target.name === "image") {
      // Store the selected image file
      setFormData({ ...formData, image: event.target.files[0] });
    } else {
      setFormData({ ...formData, [event.target.name]: event.target.value });
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const productId = "replace_with_product_id"; // Replace with the actual product ID

      // Create a form data object to send the updated product data
      const updatedFormData = new FormData();
      updatedFormData.append("title", formData.title);
      updatedFormData.append("price", formData.price);
      updatedFormData.append("description", formData.description);
      updatedFormData.append("category", formData.category);
      updatedFormData.append("image", formData.image);

      // Make a PUT request to the API endpoint
      const response = await axios.put(
        `http://localhost:8000/product/${id}`,
        updatedFormData
      );

      // Handle the response as desired (e.g., display a success message)
      console.log(response.data);
    } catch (error) {
      // Handle any errors that occurred during the API request
      console.log(error);
    }
  };

  return (
    <div>
      <h2>Update Product</h2>
      <form onSubmit={handleSubmit}>
        <label>Title:</label>
        <input
          type="text"
          name="title"
          value={formData.title}
          onChange={handleInputChange}
        />
        <br />

        <label>Price:</label>
        <input
          type="text"
          name="price"
          value={formData.price}
          onChange={handleInputChange}
        />
        <br />

        <label>Description:</label>
        <input
          type="text"
          name="description"
          value={formData.description}
          onChange={handleInputChange}
        />
        <br />

        <label>Category:</label>
        <input
          type="text"
          name="category"
          value={formData.category}
          onChange={handleInputChange}
        />
        <br />

        <label>Image:</label>
        <input type="file" name="image"  onChange={handleInputChange} />
        <br />
        <button type="submit">Update</button>
      </form>
    </div>
  );
};

export default UpdateProduct;
