import React, { useEffect, useState } from "react";
import { useParams } from 'react-router-dom';

const UpdateProduct = () => {
  const { id } = useParams();

  const [productData, setProductData] = useState(null);
  const [formData, setFormData] = useState({
    title: "",
    price: "",
    description: "",
    image: null,
    category: ""
  });

  useEffect(() => {
    // Fetch the existing data using an API endpoint
    const fetchProductData = async () => {
      try {
        const response = await fetch("http://localhost:8000/Products/${pharams:id}");
        const data = await response.json();
        setProductData(data);

        // Set the form fields with the existing data
        setFormData({
          title: data.title,
          price: data.price,
          description: data.description,
          image: null,
          category: data.category
        });
      } catch (error) {
        console.error(error);
      }
    };

    fetchProductData();
  }, []);

  const handleInputChange = (event) => {
    const { name, value, files } = event.target;
    if (name === "image") {
      setFormData((prevState) => ({ ...prevState, image: files[0] }));
    } else {
      setFormData((prevState) => ({ ...prevState, [name]: value }));
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const updatedFormData = new FormData();
      updatedFormData.append("title", formData.title);
      updatedFormData.append("price", formData.price);
      updatedFormData.append("description", formData.description);
      updatedFormData.append("image", formData.image);
      updatedFormData.append("category", formData.category);

      const response = await fetch("http://localhost:8000/Products/22", {
        method: "PUT",
        body: updatedFormData
      });

      const data = await response.json();
      setProductData(data);
      console.log(updatedFormData);
      console.log(formData);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <section className="vh-100">
      <div className="mask d-flex align-items-center h-100 gradient-custom-3">
        <div className="container h-95">
          <div className="row d-flex justify-content-center align-items-center h-100">
            <div className="col-12 col-md-9 col-lg-7 col-xl-6">
              <div className="card border rounded-lg border-info">
                <div className="card-body p-5">
                  <h2 className="text-center mb-5">Update Product</h2>
                  {productData && (
                    <form onSubmit={handleSubmit}>
                      <div className="form-group form-outline mb-3">
                        <label htmlFor="title">Title</label>
                        <input
                          type="text"
                          className="form-control form-outline mb-3"
                          id="title"
                          name="title"
                          value={formData.title}
                          onChange={handleInputChange}
                        />
                      </div>
                      <div className="form-group form-outline mb-3">
                        <label htmlFor="price">Price</label>
                        <input
                          type="number"
                          className="form-control form-outline mb-3"
                          id="price"
                          name="price"
                          value={formData.price}
                          onChange={handleInputChange}
                        />
                      </div>
                      <div className="form-group form-outline mb-3">
                        <label htmlFor="description">Description</label>
                        <textarea
                          className="form-control"
                          id="description"
                          name="description"
                          value={formData.description}
                          onChange={handleInputChange}
                        ></textarea>
                      </div>
                      <div className="form-group form-outline mb-3">
                        <label htmlFor="category">Category</label>
                        <input
                          type="text"
                          className="form-control"
                          id="category"
                          name="category"
                          value={formData.category}
                          onChange={handleInputChange}
                        />
                      </div>
                      <div className="form-outline mb-3">
                        <label className="form-label">
                          Upload Product Image
                        </label>
                        <input
                          type="file"
                          className="form-control border border-info form-control-lg"
                          id="exampleFormControlFile1"
                          name="image"
                          accept="image/*"
                          onChange={handleInputChange}
                        />
                      </div>
                      <div className="d-flex justify-content-between">
                        <button type="submit" className="btn btn-info mr-2">
                          Update Product
                        </button>
                        <button type="reset" className="btn btn-secondary ml-2">
                          Reset
                        </button>
                      </div>
                    </form>
                  )}
                </div>
              </div>
            </div>
            {productData && (
              <div className="col-12 col-md-9 col-lg-7 col-xl-6 mt-4">
                <div className="card border rounded-lg border-info">
                  <div className="card-body p-5">
                    <h2 className="text-center mb-5">Previous Data</h2>
                    <img
                      src={productData.image}
                      alt="Previous Product "
                      className="img-fluid mb-3"
                    />
                    <h5 className="mb-3">Title: {productData.title}</h5>
                    <p className="mb-3">Price: {productData.price}</p>
                    <p className="mb-3">
                      Description: {productData.description}
                    </p>
                    <p className="mb-3">Category: {productData.category}</p>
                  </div>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </section>
  );
};

export default UpdateProduct;
