import React, { useState } from 'react';
import axios from 'axios';

const AddProducts = () => {
  const [formData, setFormData] = useState({
    title: '',
    price: '',
    description: '',
    category: '',
    image: ''
  });

  const handleChange = (event) => {
    setFormData({ ...formData, [event.target.name]: event.target.value });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.post('http://localhost:8000/add-product', formData);
      console.log(response.data);
      setFormData({
        title: '',
        price: '',
        description: '',
        category: '',
        image: ''
      });

    if (response.status === 201) {
      const { image } = response.data;
      const filename = image.split('\\').pop(); // Extract the filename from the path
      console.log('File name:', filename);
      // Do something with the file name in your React component
    } else {
      console.error('Error:', response.status);
    }

    } catch (error) {
      console.error(error);
    }
  };

  return (
    <section className="vh-100">
      <div className="mask d-flex align-items-center h-100 gradient-custom-3">
        <div className="container h-95">
          <div className="row d-flex justify-content-center align-items-center h-100">
            <div className="col-12 col-md-9 col-lg-7 col-xl-6">
              <div className="card  border rounded-lg border-info">
                <div className="card-body p-5 ">
                  <h2 className="text-center mb-5">Add Product</h2>
                  <form onSubmit={handleSubmit}>
                    <div className="form-group form-outline mb-3">
                      <label htmlFor="title">Title</label>
                      <input
                        type="text"
                        className="form-control form-outline mb-3"
                        id="title"
                        name="title"
                        value={formData.title}
                        onChange={handleChange}
                      />
                    </div>
                    <div className="form-group form-outline mb-3">
                      <label htmlFor="price">Price</label>
                      <input
                        type="number"
                        className="form-control form-outline mb-3"
                        id="price"
                        name="price"
                        value={formData.price}
                        onChange={handleChange}
                      />
                    </div>
                    <div className="form-group form-outline mb-3">
                      <label htmlFor="description">Description</label>
                      <textarea
                        className="form-control"
                        id="description"
                        name="description"
                        value={formData.description}
                        onChange={handleChange}
                      ></textarea>
                    </div>
                    <div className="form-group form-outline mb-3">
                      <label htmlFor="category">Category</label>
                      <input
                        type="text"
                        className="form-control"
                        id="category"
                        name="category"
                        value={formData.category}
                        onChange={handleChange}
                      />
                    </div>
                    <div className="form-outline mb-3">
                      <label className="form-label">Upload Product Image</label>
                      <input
                        type="file"
                        className="form-control border border-info form-control-lg"
                        id="exampleFormControlFile1"
                        name="image"
                        value={formData.image}
                        onChange={handleChange}
                      />
                    </div>
                    <div className="d-flex justify-content-between ">
                      <button type="submit" className="btn btn-info mr-2">
                        Add Product
                      </button>
                      <button type="reset" className="btn btn-secondary ml-2">
                        Reset
                      </button>
                    </div>
                  </form>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default AddProducts;
