import React, { useEffect } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';

const UpdateProduct = () => {
    const [name, setName] = React.useState('');
    const [price, setPrice] = React.useState('');
    const [category, setCategory] = React.useState('');
    const [company, setCompany] = React.useState('');
    const params = useParams();
    const navigate = useNavigate();

    useEffect(() => {
        getProductDetails();
    }, [])

    const getProductDetails = async () => {
        console.warn(params);
        let result = await fetch(`https://fakestoreapi.com/products/${params.id}`);
        result = await result.json();
        setName(result.name)
        setPrice(result.price)
        setCategory(result.category)
        setCompany(result.company)
    }

    const updateProduct = async () => {
        console.warn(name, price, category, company)
        let result = await fetch(`https://fakestoreapi.com/products/${params.id}`, {
            method: "put",
            body: JSON.stringify({ name, price, category, company }),
            headers: {
                "Content-Type": "application/json"
            }
        });
        result = await result.json()
        console.warn(result)
        navigate('/')
    }

    return (
        <section class="vh-100 ">
        <div class="mask d-flex align-items-center h-100 gradient-custom-3">
          <div class="container h-95">
            <div class="row d-flex justify-content-center align-items-center h-100">
              <div class="col-12 col-md-9 col-lg-7 col-xl-6">
                <div class="card  border rounded-lg border-info">
                  <div class="card-body p-5 ">
                    <h2 class="text-center mb-5">Add Product</h2>
                    <form onSubmit={updateProduct}>
                      <div className="form-group form-outline mb-3">
                        <label htmlFor="title">Title</label>
                        <input
                          type="text"
                          className="form-control form-outline mb-3"
                          id="title"
                          name="title"
                          value={formData.title}
                          onChange={handleChange}
                        />
                      </div>
                      <div className="form-group form-outline mb-3">
                        <label htmlFor="price">Price</label>
                        <input
                          type="number"
                          className="form-control form-outline mb-3"
                          id="price"
                          name="price"
                          value={formData.price}
                          onChange={handleChange}
                        />
                      </div>
                      <div className="form-group form-outline mb-3">
                        <label htmlFor="description">Description</label>
                        <textarea
                          className="form-control"
                          id="description"
                          name="description"
                          value={formData.description}
                          onChange={handleChange}
                        ></textarea>
                      </div>
                      <div className="form-group form-outline mb-3">
                        <label htmlFor="category">Category</label>
                        <input
                          type="text"
                          className="form-control"
                          id="category"
                          name="category"
                          value={formData.category}
                          onChange={handleChange}
                        />
                      </div>
                      <div class="form-outline mb-3">
                        <label class="form-label">Upload Product Image</label>
                        <input
                          type="file"
                          class="form-control border border-info form-control-lg"
                          id="exampleFormControlFile1"
                          value={formData.image}
                          onChange={handleChange}
                        />
                      </div>
                      <div className="d-flex justify-content-between ">
                        <button type="submit" className="btn btn-info mr-2">
                          Add Product
                        </button>
                        <button type="reset" className="btn btn-secondary ml-2">
                          Reset
                        </button>
                      </div>
                    </form>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    )
}

export default UpdateProduct;
