import React, { useState, useEffect } from 'react'


const Products = () => {

    const [data, setData] = useState([]);
    const [filter, setFilter] = useState([data]);
    const [loading, setLoadin] = useState([false]);
    let componentMounted = true;

    useEffect(() => {
        const getProducts = async () => {
            setLoadin(true);
            const respone = await fetch("http://fakestoreapi.com/products");
            if (componentMounted) {
                setData(await respone.clone().json());
                setFilter(await respone.json());
                setLoadin(false);
                console.log(filter)
            }
            return () => {
                componentMounted = false;
            }
        }
        getProducts();
    }, []);

    const Loading = () => {
        return (
            <>
                Loading...
            </>
        )
    }
    const ShowProducts = () => {
        return (
            <>
                <div className="button">
                    <button className='btn btn-outline-dark'>All</button>
                    <button className='btn btn-outline-dark'>Men's Clothing</button>
                    <button className='btn btn-outline-dark'>Women's Clothing</button>
                    <button className='btn btn-outline-dark'>Jewelery</button>
                    <button className='btn btn-outline-dark'>Electronic</button>
                </div>
            </>
        );
    };

    return (
        <div>
            <div className="container my-5 py-5">
                <div className="row">
                    <div className="col-12 mb-5">
                        <h1 className='display-6 fw-bolder text-center'>Latest Products</h1>
                        <hr />
                    </div>
                </div>
                <div className="row justify-content-center">
                    {loading ? <Loading /> : <ShowProducts />}
                </div>
            </div>
        </div>
    )
}

export default Products;
