import React from "react";
import {useState} from 'react';


const UpdateProduct = () => {
  const [title, setTitle] = React.useState("");
  const [price, setPrice] = React.useState("");
  const [description, setDescription] = React.useState("");
  const [category, setCategory] = React.useState("");
  const [image, setImage] = React.useState("");

  return (
    <section class="vh-100 ">
      <div class="mask d-flex align-items-center h-100 gradient-custom-3">
        <div class="container h-95">
          <div class="row d-flex justify-content-center align-items-center h-100">
            <div class="col-12 col-md-9 col-lg-7 col-xl-6">
              <div class="card  border rounded-lg border-info">
                <div class="card-body p-5 ">
                  <h2 class="text-center mb-5">Update Product</h2>
                  <form>
                    <div class="form-outline mb-3">
                      <label htmlFor="title">Title</label>
                      <input
                        type="text"
                        value={title}
                        onChange={(e) => {
                          setTitle(e.target.value);
                        }}
                        class="form-control border border-info form-control-lg"
                      />
                    </div>
                    <div class="form-outline mb-3">
                      <label class="form-label">Enter Product Price</label>
                      <input
                        type="text"
                        value={price}
                        onChange={(e) => {
                          setPrice(e.target.value);
                        }}
                        class="form-control border border-info form-control-lg"
                      />
                    </div>
                    <div class="form-outline mb-3">
                      <label htmlFor="description">Description</label>
                      <textarea
                        type="text"
                        value={description}
                        onChange={(e) => {
                          setDescription(e.target.value);
                        }}
                        class="form-control border border-info form-control-lg"
                      ></textarea>
                    </div>
                    <div class="form-outline mb-3">
                      <label htmlFor="category">Category</label>
                      <input
                        type="text"
                        value={category}
                        onChange={(e) => {
                          setCategory(e.target.value);
                        }}
                        class="form-control border border-info form-control-lg"
                      />
                    </div>
                    <div class="form-outline mb-3">
                      <label class="form-label">Upload Product Image</label>
                      <input
                        type="file"
                        class="form-control border border-info form-control-lg"
                        id="exampleFormControlFile1"
                        // value={image}
                        onChange={(e) => {
                          setImage(e.target.value);
                        }}
                      />
                    </div>
                    <div class="d-flex justify-content-center">
                      <Link onClick={updateProduct} to={"/adminProducts"}>
                        <button
                          type="button "
                          class="btn btn-success bg-info btn-block btn-lg gradient-custom-4 text-body"
                        >
                          Update Product
                        </button>
                      </Link>
                    </div>
                  </form>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default UpdateProduct;
