import React from 'react';

const AddProduct = () => {
    const [title, setTitle] = React.useState('');
    const [price, setPrice] = React.useState('');
    const [category, setCategory] = React.useState('');
    const [description, setDescription] = React.useState('');
    const [image, setImage] = React.useState('');
    console.log(image);

    const AddProduct = async () => {

        const userId = JSON.parse(localStorage.getItem('user'))._id;
        let result = await fetch("http://localhost:8000/add-product", {
            method: "post",
            body: JSON.stringify({ title, price, category, description }),
            headers: {
                "Content-Type": "application/json"
            }
        });
        result = await result.json();
        console.warn(result);
    }

    return (
        <section class="vh-100 ">
            <div class="mask d-flex align-items-center h-100 gradient-custom-3">
                <div class="container h-95">
                    <div class="row d-flex justify-content-center align-items-center h-100">
                        <div class="col-12 col-md-9 col-lg-7 col-xl-6">
                            <div class="card  border rounded-lg border-info" >
                                <div class="card-body p-5 ">
                                    <h2 class="text-center mb-5">Add Product</h2>
                                    <form>
                                        <div class="form-outline mb-3">
                                            <label class="form-label" >Enter Product title</label>
                                            <input type="text" value={title} onChange={(e) => { setTitle(e.target.value) }} placeholder='Enter product title' class="form-control border border-info form-control-lg" />

                                        </div>

                                        <div class="form-outline mb-3">
                                            <label class="form-label">Enter Product Price</label>
                                            <input type="text" value={price} onChange={(e) => { setPrice(e.target.value) }} placeholder='Enter product price' class="form-control border border-info form-control-lg" />


                                        </div>

                                        <div class="form-outline mb-3">
                                            <label class="form-label" >Enter Product Category</label>
                                            <input type="text" value={category} onChange={(e) => { setCategory(e.target.value) }} placeholder='Enter product category' class="form-control border border-info form-control-lg" />


                                        </div>
                                        <div class="form-outline mb-3">
                                            <label class="form-label">Enter Product description</label>
                                            <input type="text" value={description} onChange={(e) => { setDescription(e.target.value) }} placeholder='Enter product description' class="form-control border border-info form-control-lg" />


                                        </div>
                                        <div class="form-outline mb-3">
                                            <label class="form-label">Upload Product Image</label>
                                            <input onChange={(e) => setImage(e.target.files[0])} type="file" class="form-control border border-info form-control-lg" />


                                        </div>
                                        <div class="d-flex justify-content-center">
                                            <button type="button " onClick={AddProduct}
                                                class="btn btn-success bg-info btn-block btn-lg gradient-custom-4 text-body">Add Product</button>
                                        </div>
                                    </form>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </section>
    )
}

export default AddProduct;
