import React, { useState } from "react";
import axios from "axios";


const AddProducts = () => {
  const [formData, setFormData] = useState({
    title: "",
    price: "",
    description: "",
    category: "",
    image: "",
  });

  const handleChange = (event) => {
    setFormData({ ...formData, [event.target.name]: event.target.value });
  };


  const handleSubmit = (event) => {
    event.preventDefault();
    axios.post("/add-product", formData)
      .then((response) => {
        console.log(response.data);
      })
      .catch((error) => {
        console.error(error);
      });
  };

  console.log(formData);

  return (
    <section class="vh-100 ">
      <div class="mask d-flex align-items-center h-100 gradient-custom-3">
        <div class="container h-95">
          <div class="row d-flex justify-content-center align-items-center h-100">
            <div class="col-12 col-md-9 col-lg-7 col-xl-6">
              <div class="card  border rounded-lg border-info">
                <div class="card-body p-5 ">
                  <h2 class="text-center mb-5">Add Product</h2>
                  <form onSubmit={handleSubmit}>
                    <div className="form-group form-outline mb-3">
                      <label htmlFor="title">Title</label>
                      <input
                        type="text"
                        className="form-control form-outline mb-3"
                        id="title"
                        name="title"
                        value={formData.title}
                        onChange={handleChange}
                      />
                    </div>
                    <div className="form-group form-outline mb-3">
                      <label htmlFor="price">Price</label>
                      <input
                        type="number"
                        className="form-control form-outline mb-3"
                        id="price"
                        name="price"
                        value={formData.price}
                        onChange={handleChange}
                      />
                    </div>
                    <div className="form-group form-outline mb-3">
                      <label htmlFor="description">Description</label>
                      <textarea
                        className="form-control"
                        id="description"
                        name="description"
                        value={formData.description}
                        onChange={handleChange}
                      ></textarea>
                    </div>
                    <div className="form-group form-outline mb-3">
                      <label htmlFor="category">Category</label>
                      <input
                        type="text"
                        className="form-control"
                        id="category"
                        name="category"
                        value={formData.category}
                        onChange={handleChange}
                      />
                    </div>
                    <div class="form-outline mb-3">
                      <label class="form-label">Upload Product Image</label>
                      <input
                        type="file"
                        class="form-control border border-info form-control-lg"
                        id="exampleFormControlFile1"
                        value={formData.image}
                        onChange={handleChange}
                      />
                    </div>
                    <div className="d-flex justify-content-between ">
                      <button type="submit" className="btn btn-info mr-2">
                        Add Product
                      </button>
                      <button type="reset" className="btn btn-secondary ml-2">
                        Reset
                      </button>
                    </div>
                  </form>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default AddProducts;
