import React, { useState,useEffect } from "react";
import { Container, Form, Button } from "react-bootstrap";
import { useNavigate } from "react-router";

const AdminLogin = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();


  useEffect(() => {
    const auth = localStorage.getItem("user");
    if (auth) {
      // navigate("/adminProducts");
    }
  });
  const handleLogin = async () => {
    console.warn("username,password", username, password);
     fetch("http://localhost:8000/adminlogin", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        username: username,
        password: password,
      }),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error(response.statusText);
        }
        return response.json();
      })
      .then((data) => {
        if (data.auth) {
            localStorage.setItem("user", JSON.stringify(data.user));
            localStorage.setItem("token", JSON.stringify(data.auth));
            // navigate("/adminProducts");
          } else {
            alert("Please Enter Correct Details... ");
          }
      })
      .catch((error) => {
        // Handle login error
      });
    }

  return (
    <section className="vh-100">
      <div className="mask d-flex align-items-center h-100 gradient-custom-3">
        <div className="container h-95">
          <div className="row d-flex justify-content-center align-items-center h-100">
            <div className="col-12 col-md-9 col-lg-7 col-xl-6">
              <div className="card  border rounded-lg border-info">
                <div className="card-body p-5 ">
                  <h2 className="text-center mb-5">Admin Login</h2>
                  <Form onSubmit={handleLogin}>
                    <Form.Group controlId="formUsername">
                      <Form.Label>Username</Form.Label>
                      <Form.Control
                        type="text"
                        placeholder="Enter username"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                      />
                    </Form.Group>

                    <Form.Group controlId="formPassword">
                      <Form.Label>Password</Form.Label>
                      <Form.Control
                        type="password"
                        placeholder="Enter password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                      />
                    </Form.Group>

                    <div className="d-flex m-4 p-3 justify-content-between ">
                      <Button
                        variant="primary"
                        type="submit"
                        className="w-100  btn btn-info m-3 p-3"
                      >Login</Button>
                    </div>
                  </Form>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};


export default AdminLogin;
