import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

import axios from "axios";

function UpdateProduct() {

  const { id } = useParams();
  const [product, setProduct] = useState(null);

  useEffect(() => {
    async function getProductDetails() {
      try {
        const response = await axios.get(
          `https://fakestoreapi.com/products/${id}`
        );
        setProduct(response.data);
      } catch (error) {
        console.log(error);
      }
    }
    getProductDetails();
  }, [id]);

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setProduct({ ...product, [name]: value });
  };

  const handleUpdateProduct = async () => {
    try {
      const response = await axios.put(
        `https://fakestoreapi.com/products/${id}`,
        product
      );
      console.log(response.data);
      history.push("/");
    } catch (error) {
      console.log(error);
    }
  };

  if (!product) {
    return <div>Loading...</div>;
  }

    return (
        <section class="vh-100 ">
        <div class="mask d-flex align-items-center h-100 gradient-custom-3">
          <div class="container h-95">
            <div class="row d-flex justify-content-center align-items-center h-100">
              <div class="col-12 col-md-9 col-lg-7 col-xl-6">
                <div class="card  border rounded-lg border-info">
                  <div class="card-body p-5 ">
                    <h2 class="text-center mb-5">Add Product</h2>
                    <form>
                      <div className="form-group form-outline mb-3">
                        <label htmlFor="title">Title</label>
                        <input
                          type="text"
                          className="form-control form-outline mb-3"
                          id="title"
                          name="title"
                          value={product.title}
                          onChange={handleInputChange}
                        />
                      </div>
                      <div className="form-group form-outline mb-3">
                        <label htmlFor="price">Price</label>
                        <input
                          type="number"
                          className="form-control form-outline mb-3"
                          id="price"
                          name="price"
                          value={product.price}
                          onChange={handleInputChange}
                        />
                      </div>
                      <div className="form-group form-outline mb-3">
                        <label htmlFor="description">Description</label>
                        <textarea
                          className="form-control"
                          id="description"
                          name="description"
                          value={product.description}
                          onChange={handleInputChange}
                        ></textarea>
                      </div>
                      <div className="form-group form-outline mb-3">
                        <label htmlFor="category">Category</label>
                        <input
                          type="text"
                          className="form-control"
                          id="category"
                          name="category"
                          value={product.category}
                          onChange={handleInputChange}
                        />
                      </div>
                      <div class="form-outline mb-3">
                        <label class="form-label">Upload Product Image</label>
                        <input
                          type="file"
                          class="form-control border border-info form-control-lg"
                          id="exampleFormControlFile1"
                          value={product.image}
                          onChange={handleInputChange}
                        />
                      </div>
                      <div className="d-flex justify-content-between ">
                        <button type="submit" className="btn btn-info mr-2">
                          Add Product
                        </button>
                        <button type="reset" onClick={handleUpdateProduct} className="btn btn-secondary ml-2">
                          Reset
                        </button>
                      </div>
                    </form>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    )
}

export default UpdateProduct;
